mapper.insert:
Insert Into t_feed

    iuser = #{iuser}

        , contents = #{contents}


        , location = #{location}

mapper.select:
Select B.ifeed ,B.contents ,B.location ,B.iuser as writerIuser ,A.nm as writerNm , B.created_at as createdAt ,if(C.iuser is null,0,1) as isFav From t_user A join t_feed B on A.iuser = B.iuser
And B.iuser = #{targetIuser}
Left Join t_feed_fav C on B.ifeed = C.ifeed AND C.iuser = #{loginedIuser} Order By B.ifeed desc LIMIT #{startIdx}, #{rowCount}
